package com.syntax.class02;

public class WaysToCreateVariable {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		/*1way to create a variable
		 * datatipe name = value 
		 */
		
		//declare a variable temperature and initialized it
		int temperature=60;
		System.out.println(temperature);
		
		
		temperature=70;
		System.out.println(temperature);
		
		int sun; //create a variable
		sun=30;//assign the value
		
		sun=50;//reassigning the value
		System.out.println(sun);
		
		int num1, num2, num3;
		num1=10;
		num2=20;
		num3=30;
		System.out.println();
		
		
		
		
		

	}

}
