package com.syntax.class04;

public class OperatorsDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
int num=10;
int num2=10;
System.out.println(num+num2);
System.out.println(num>=num2);// num>num2->false; num=num2->True false+true=true
//Whole numbers 0-infinity. Negative infinity to Positive infinity
System.out.println(Long.MAX_VALUE);//given us the maximum range
System.out.println(Long.MIN_VALUE);
System.out.println(num/num2);
System.out.println(num%num2);
System.out.println(10.0/3);
System.out.println((int)10.0/3);
System.out.println(10.0*3);
	}

}
