package com.syntax.class08;

public class NestedLoopExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
for(int a=1; a<=2; a++) {
	for(int b=0; b<=4;b++) {
		for (int c=0; c<=5; c++) {
		  System.out.println(a+""+b+":"+c);
		}
		}
	}

}



}

		
	
			
		
	
		
		
		
	


