package com.syntax.class09;

public class NestedLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		for(int i=1; i<=5; i++) {
			
			System.out.println("hello "+i);//5
			for(int y=1; y<=3; y++) {
				System.out.println("Bye" +y);//5
				break;
			}
		}
		
			
		
		
		
		
		
		
	}

}
